const naturalCompare = require("natural-compare");

function intersectSafe(array1, array2) {
  if (!Array.isArray(array1) || !Array.isArray(array2)) {
    throw new Error("Both arguments must be arrays.");
  }

  const array1HasValidElements = array1.every((element) =>
    typeof element === "number" || typeof element === "string"
  );
  const array2HasValidElements = array2.every((element) =>
    typeof element === "number" || typeof element === "string"
  );

  if (!array1HasValidElements || !array2HasValidElements) {
    throw new Error(
      "All elements in both arrays must be either numbers or strings."
    );
  }

  let result = [];
  let i = 0;
  let j = 0;

  while (i < array1.length && j < array2.length) {
    if (array1[i] < array2[j]) {
      i++;
    } else if (array1[i] > array2[j]) {
      j++;
    } else {
      result.push(array1[i]);
      i++;
      j++;
    }
  }

  return result;
}

module.exports = {
  intersectSafe,
};
