import { DXT } from "./docxtemplater";

export interface Template {
  content: string;
}

export interface RenderOptions {
  modules?: any;
}

declare class TxtTemplater {
  private _compiledTemplate;

  /**
   * Create TxtTemplater instance
   *
   * @param content the template that you want to use
   * @param options `modules` and other options
   */
  constructor(template: Template, options?: RenderOptions);

  /**
   * Compile the template
   */
  compile(): void;

  /**
   * Render the template with the given data
   *
   * @param data the data to use for rendering
   */
  render(data?: any): string;

  /**
   * Static property to access the `DXT` module
   */
  static DXT = DXT;
}

export default TxtTemplater;
