import * as DXT from "./docxtemplater";

interface ConstructorOptions extends DXT.ConstructorOptions {}

class TxtTemplater {
  private template: DXT.DocxTemplate;

  constructor(content: string, options?: ConstructorOptions) {
    this.template = new DXT.DocxTemplate(content, options);
  }

  public render(data?: any): string {
    return this.template.render(data);
  }
}

export default TxtTemplater;
